import React from "react";
import Header from "./components/Header";
import StatsPanel from "./components/StatsPanel";
import ChartCard from "./components/ChartCard";
import ServerList from "./components/ServerList";
import {
  alerts,
  resourceUsage,
  networkTraffic,
} from "./data/mockData";

const App: React.FC = () => {
  return (
    <div style={styles.container}>
      <div style={styles.background}>
        <div style={styles.gradientOverlay} />
      </div>
      <Header />
      <main style={styles.content}>
        <StatsPanel alerts={alerts} />

        <section className="animate-fade-in" style={styles.section}>
          <div style={styles.sectionHeader}>
            <h2 style={styles.sectionTitle}>Resource Monitoring</h2>
            <div style={styles.sectionSubtitle}>Real-time system metrics</div>
          </div>
          <div style={styles.chartsGrid}>
            <ChartCard
              title="CPU Usage"
              type="line"
              data={resourceUsage}
              dataKey="cpu"
              color="#3498db"
            />
            <ChartCard
              title="RAM Usage"
              type="line"
              data={resourceUsage}
              dataKey="ram"
              color="#27ae60"
            />
            <ChartCard
              title="Disk Usage"
              type="bar"
              data={resourceUsage}
              dataKey="disk"
              color="#e74c3c"
            />
            <ChartCard
              title="Network Traffic"
              type="line"
              data={networkTraffic}
              dataKey="incoming"
              color="#9b59b6"
            />
          </div>
        </section>

        <section className="animate-fade-in" style={styles.section}>
          <div style={styles.sectionHeader}>
            <h2 style={styles.sectionTitle}>Server Status</h2>
            <div style={styles.sectionSubtitle}>Active server instances</div>
          </div>
          <ServerList />
        </section>
      </main>
    </div>
  );
};

const styles = {
  container: {
    minHeight: "100vh",
    position: "relative" as const,
    overflow: "hidden",
    backgroundColor: "#f0f4f8",
  },
  background: {
    position: "fixed" as const,
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    zIndex: -1,
  },
  gradientOverlay: {
    position: "absolute" as const,
    top: 0,
    left: 0,
    right: 0,
    height: "100%",
    background:
      "linear-gradient(180deg, rgba(255,255,255,0.8) 0%, rgba(255,255,255,0.3) 100%)",
  },
  content: {
    maxWidth: "1400px",
    margin: "0 auto",
    padding: "2rem",
  },
  section: {
    marginBottom: "3rem",
  },
  sectionHeader: {
    marginBottom: "2rem",
  },
  sectionTitle: {
    fontSize: "1.8rem",
    fontWeight: 600,
    color: "#1a365d",
    marginBottom: "0.5rem",
  },
  sectionSubtitle: {
    fontSize: "1rem",
    color: "#4a5568",
    letterSpacing: "0.5px",
  },
  chartsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(350px, 1fr))",
    gap: "1.5rem",
  },
};

export default App;
