import React, { useEffect, useState } from "react";
import Header from "components/Header";
import StatsPanel from "components/StatsPanel";
import ChartCard from "components/ChartCard";
import ServerList from "components/ServerList";
import { fetchServers, Server, fetchAlertCounts } from "./services/api";

interface AlertCounts {
  critical: number;
  medium: number;
  low: number;
}

const App: React.FC = () => {
  const [servers, setServers] = useState<Server[]>([]);
  const [alerts, setAlerts] = useState<AlertCounts>({
    critical: 0,
    medium: 0,
    low: 0,
  });
  const [resourceUsage, setResourceUsage] = useState<any[]>([]);
  const [networkTraffic, setNetworkTraffic] = useState<any[]>([]);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const serverData = await fetchServers(); // Fetch servers from API
        const updatedServers = serverData.map((server) => ({
          ...server,
          ip_address: server.ip_address || "Unknown IP",
          location: server.location || "Unknown Location",
          last_checked: server.last_checked || new Date().toISOString(),
        }));
        setServers(updatedServers); // Ensure serverData matches the Server type
      } catch (error) {
        console.error("Error fetching servers:", error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log("Fetching alert counts..."); // Debugging log
        const alertCounts = await fetchAlertCounts();
        console.log("Alert counts fetched:", alertCounts); // Debugging log
        setAlerts(alertCounts);
      } catch (error) {
        console.error("Error fetching alert counts:", error);
        setError("Failed to fetch alert counts. Please try again later.");
      }
    };

    fetchData();
  }, []);

  if (error) {
    return <div style={{ color: "red", textAlign: "center" }}>{error}</div>;
  }

  return (
    <div style={styles.container}>
      <Header />
      <main style={styles.content}>
        <StatsPanel alerts={alerts} />
        <section>
          <h2>Resource Monitoring</h2>
          <div style={styles.grid}>
            <ChartCard
              title="CPU Usage"
              type="line"
              data={resourceUsage}
              dataKey="cpu"
              color="#3498db"
            />
            <ChartCard
              title="RAM Usage"
              type="line"
              data={resourceUsage}
              dataKey="ram"
              color="#27ae60"
            />
            <ChartCard
              title="Disk Usage"
              type="bar"
              data={resourceUsage}
              dataKey="disk"
              color="#e74c3c"
            />
            <ChartCard
              title="Network Traffic"
              type="line"
              data={networkTraffic}
              dataKey="incoming"
              color="#9b59b6"
            />
          </div>
        </section>

        <section>
          <h2>Server Status</h2>
          <ServerList servers={servers} />
        </section>
      </main>
    </div>
  );
};

const styles = {
  container: {
    minHeight: "100vh",
    backgroundColor: "#f4f4f4",
    padding: "2rem",
  },
  content: {
    maxWidth: "1200px",
    margin: "0 auto",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "1rem",
  },
};

export default App;
