import React, { useEffect, useState } from "react";
import Header from "./components/Header";
import StatsPanel from "./components/StatsPanel";
import ChartCard from "./components/ChartCard";
import ServerList from "./components/ServerList";

interface Server {
  id: number;
  name: string;
  ip_address: string;
  location: string;
  status: string;
  last_checked: string;
}

interface AlertCounts {
  critical: number;
  medium: number;
  low: number;
}

const App: React.FC = () => {
  const [servers, setServers] = useState<Server[]>([]);
  const [alerts, setAlerts] = useState<AlertCounts>({
    critical: 0,
    medium: 0,
    low: 0,
  });
  const [resourceUsage, setResourceUsage] = useState<any[]>([]);
  const [networkTraffic, setNetworkTraffic] = useState<any[]>([]);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    try {
      const mockServers = [
        { id: 1, name: "Server A", ip_address: "192.168.1.1", location: "Data Center 1", status: "Online", last_checked: "10:00 AM" },
        { id: 2, name: "Server B", ip_address: "192.168.1.2", location: "Data Center 2", status: "Offline", last_checked: "9:45 AM" },
      ];
      setServers(mockServers);

      setAlerts({ critical: 1, medium: 2, low: 5 });

      const timestamps = ["10:00", "10:10", "10:20"];
      setResourceUsage(
        timestamps.map((t, i) => ({
          timestamp: t,
          cpu: Math.random() * 100,
          ram: Math.random() * 100,
          disk: Math.random() * 100,
        }))
      );

      setNetworkTraffic(
        timestamps.map((t) => ({
          timestamp: t,
          incoming: Math.random() * 1000,
        }))
      );
    } catch (e) {
      setError("Failed to load mock data.");
    }
  }, []);

  if (error) {
    return <div style={{ color: "red", textAlign: "center" }}>{error}</div>;
  }

  return (
    <div style={styles.container}>
      <Header />
      <main style={styles.content}>
        <StatsPanel alerts={alerts} />
        <section>
          <h2>Resource Monitoring</h2>
          <div style={styles.grid}>
            <ChartCard
              title="CPU Usage"
              type="line"
              data={resourceUsage}
              dataKey="cpu"
              color="#3498db"
            />
            <ChartCard
              title="RAM Usage"
              type="line"
              data={resourceUsage}
              dataKey="ram"
              color="#27ae60"
            />
            <ChartCard
              title="Disk Usage"
              type="bar"
              data={resourceUsage}
              dataKey="disk"
              color="#e74c3c"
            />
            <ChartCard
              title="Network Traffic"
              type="line"
              data={networkTraffic}
              dataKey="incoming"
              color="#9b59b6"
            />
          </div>
        </section>

        <section>
          <h2>Server Status</h2>
          <ServerList servers={servers} />
        </section>
      </main>
    </div>
  );
};

const styles = {
  container: {
    minHeight: "100vh",
    backgroundColor: "#f4f4f4",
    padding: "2rem",
  },
  content: {
    maxWidth: "1200px",
    margin: "0 auto",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "1rem",
  },
};

export default App;
