import React, { useEffect, useState } from "react";
import Header from "components/Header";
import StatsPanel from "components/StatsPanel";
import ChartCard from "components/ChartCard";
import ServerList from "components/ServerList";
import { fetchServers, Server, fetchAlertCounts, fetchServerMetrics } from "./services/api";

interface AlertCounts {
  critical: number;
  medium: number;
  low: number;
}

const App: React.FC = () => {
  const [servers, setServers] = useState<Server[]>([]);
  const [alerts, setAlerts] = useState<AlertCounts>({
    critical: 0,
    medium: 0,
    low: 0,
  });
  const [resourceUsage, setResourceUsage] = useState<any[]>([]);
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  const [networkTraffic, setNetworkTraffic] = useState<any[]>([]);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log("Fetching servers...");
        const serverData = await fetchServers();
        console.log("Servers fetched:", serverData);
        setServers(serverData);

        console.log("Fetching alert counts...");
        const alertCounts = await fetchAlertCounts();
        console.log("Alert counts fetched:", alertCounts);
        setAlerts(alertCounts);

        console.log("Fetching server metrics...");
        const metrics = await fetchServerMetrics(1); // Assuming server ID 1
        console.log("Server metrics fetched:", metrics);
        setResourceUsage([
          { name: "CPU", data: metrics.cpu_usage },
          { name: "RAM", data: metrics.ram_usage },
          { name: "Disk", data: metrics.disk_usage },
          { name: "Application", data: metrics.application_usage },
        ]);
      } catch (error) {
        console.error("Error fetching data:", error);
        setError("Failed to fetch data. Please try again later.");
      }
    };

    fetchData();
  }, []);

  if (error) {
    return <div style={{ color: "red", textAlign: "center" }}>{error}</div>;
  }

  return (
    <div style={styles.container}>
      <Header />
      <main style={styles.content}>
        <StatsPanel alerts={alerts} />
        <section>
          <h2>Resource Monitoring</h2>
          <div style={styles.grid}>
            <ChartCard
              title="CPU Usage"
              type="line"
              data={resourceUsage}
              dataKey="cpu"
              color="#3498db"
            />
            <ChartCard
              title="RAM Usage"
              type="line"
              data={resourceUsage}
              dataKey="ram"
              color="#27ae60"
            />
            <ChartCard
              title="Disk Usage"
              type="bar"
              data={resourceUsage}
              dataKey="disk"
              color="#e74c3c"
            />
            <ChartCard
              title="Network Traffic"
              type="line"
              data={networkTraffic}
              dataKey="incoming"
              color="#9b59b6"
            />
          </div>
        </section>

        <section>
          <h2>Server Status</h2>
          <ServerList servers={servers} />
        </section>
      </main>
    </div>
  );
};

const styles = {
  container: {
    minHeight: "100vh",
    backgroundColor: "#f4f4f4",
    padding: "2rem",
  },
  content: {
    maxWidth: "1200px",
    margin: "0 auto",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "1rem",
  },
};

export default App;
